# ddc-init-aws
docker pull docker4x/ddc-init-aws:$UCP_INIT_TAG
docker run --log-driver=json-file --name=ddc-init-aws --restart=on-failure:5 -e DYNAMODB_TABLE=$DYNAMODB_TABLE -e NODE_TYPE=$NODE_TYPE -e REGION=$AWS_REGION -e STACK_NAME=$STACK_NAME  -e INSTANCE_NAME=$INSTANCE_NAME -e INSTALL_DDC='yes' -e NODE_NAME=$HOSTNAME -e UCP_ADMIN_USER=$UCP_ADMIN_USER -e UCP_ADMIN_PASSWORD=$UCP_ADMIN_PASSWORD -e S3_BUCKET_NAME=$S3_BUCKET_NAME -e LICENSE="$LICENSE" -e UCP_ELB_HOSTNAME=$UCP_ELB_HOSTNAME -e DTR_ELB_HOSTNAME=$DTR_ELB_HOSTNAME -e APP_ELB_HOSTNAME=$APP_ELB_HOSTNAME -e MANAGER_COUNT=$MANAGER_COUNT -v /var/run/docker.sock:/var/run/docker.sock -v /usr/bin/docker:/usr/bin/docker -v /tmp/docker:/tmp/docker -e UCP_TAG=$UCP_TAG -e DTR_TAG=$DTR_TAG docker4x/ddc-init-aws:$UCP_INIT_TAG
